const fetch = require('node-fetch');

exports.updateData = function(id, servers, users, token) {
  fetch(`http://de1.bluefoxhost.com:9093/api/v1/updateBot?botId=${id}&servers=${servers}&users=${users}`, {
        method: 'post',
        headers: {
        	"Authorization": "Bearer " + token,
        }
   }).then(res => res.json()).then(json => {
   	console.log(json)
   });
}

exports.getBotData = function(id) {
  fetch(`http://de1.bluefoxhost.com:9093/api/v1/getBot?botId=${id}`, {
        method: 'get',
   }).then(res => res.json()).then(json => {
   	console.log(json);
   	return json;
   });
}